import * as Papa from 'papaparse';

export var data = [];

export function SelectFile() {
    return (
            <input type="file" id="file-selector" accept=".csv"/>
    );
}

export function AddFileListener() {
    const fileSelector = document.getElementById('file-selector');
    fileSelector.addEventListener('change', (event) => {
      const fileList = event.target.files;
    //   console.log(fileList);
      loadFile(fileList[0]);
    });
}

export async function loadFile(fileObject){
    const reader = new FileReader();
    reader.readAsText(fileObject, "UTF-8");
    reader.onload = event => {
        parseCSV(event.target.result);
    };
}


export function parseCSV(file){
    Papa.parse(file, {
        header: true,
        // columns: ["Answer","Question","Answer Letters","Question Letters","Category","Publisher","Series","Universe","Difficulty"],
        complete: function(CSVFilePlaces) {
            data = CSVFilePlaces.data
        }
    });
}


