import { Component } from "react";
import '../css/Cell.css'

class Cell extends Component{
    static defaultProps = {
        mode:0,
        letter: ''
    }
    constructor(props){
        super(props)
        this.handleToggle = this.handleToggle.bind(this)
    }
    handleToggle(){
        // toggles cells above, below, right and to left
        const [row, col] = this.props.pos
        this.props.toggle(this.props.pos)
    }
    render() {
        let classes = 'Cell' + (this.props.letter != "" ? ' Cell-Lit' : '')
        let content = this.props.letter;
        return (
            <td className={classes} onClick={this.handleToggle}>
                <p className="LitContent">
                    {content}
                </p>
            </td>
        )
    }
}

export default Cell
