import { Component } from "react";
import '../css/Cell.css'

class Cell extends Component{
    static defaultProps = {
        mode: false,
        letter: ''
    }
    constructor(props){
        super(props)
        this.handleToggle = this.handleToggle.bind(this)
    }

}

export default Cell



function handleToggle(props){
    // toggles cells above, below, right and to left
    const [row, col] = props.pos
    props.toggle(props.pos)
}

function render(props) {
    let classes = 'Cell' + (props.state ? ' Cell-Lit' : '')
    let content = props.letter;
    return (
        <td className={classes} onClick={this.handleToggle}>
            <p className="LitContent">
                {content}
            </p>
        </td>
    )
}
