import { Component, useEffect } from "react";
import Cell, { RenderCell } from "./Cell";
import '../css/Board.css'
import { AddFileListener } from "./ProcessData";


export default function Board(props) {
    var dimensions = getDimensions(props);

    return (<RenderBoard dimensions={dimensions} word={props.word} data={props.data}/>);
}

function CreateBoard(props) {
    var board = [];
    console.log(props.dimensions["width"]);
    // Initialize Board
    for (var i = 0; i < props.dimensions["height"]; i++) {
        var temp = [];

        for (var j = 0; j < props.dimensions["width"]; j++) 
            temp[j].push(Cell());

        console.log(temp);
        board.push(temp);
    }

    var center = parseInt(props.dimensions.width / 2);
    // console.log(board);
    return (
        <tr>
            {board.map((arr, index) => {
                arr.map((element, i) => {
                    <RenderCell context={element}/>
                })
            })}
        </tr>

    );
}

function ToggleBoardPosition() {

}


function RenderBoard(props) {
    // return (
    //     state.hasWon
    //     ? 
    //     <div className='winner'>
    //         <div className='neon-orange'>you</div>
    //         <div className='neon-blue'>win</div>
    //     </div>
    //     :
    //     <div>
    //         <div className='Board-title'>
    //             <div className='neon-orange'>Hero</div>
    //             <div className='neon-blue'>Trivia</div>
    //         </div>
    //         <table className='Board'>
    //             {genGridCells()}
    //         </table>

    //     </div>
    // );

    return (
        <div>
            <div className='Board-title'>
                <div className='neon-orange'>Hero</div>
                <div className='neon-blue'>Trivia</div>
            </div>
            <table className='Board'>
            {/* <th></th> */}
                <tbody>
                    <CreateBoard dimensions={props.dimensions} word={props.word} data={props.data}/>
                </tbody>
            </table>

        </div>
    );
}

function getDimensions(props) {
    var width = 0, height = 0;
    height = props.word.length;

    props.data.forEach(element => {
        if (width < element["Answer"].length)
            width = element["Answer"].length;
    });
    return {"width": width, "height": height};
}
