import { Component, useState } from "react";
import '../css/Cell.css'


export default function Cell() {
    const [state, setState] = useState(false);
    const [special, setSpecial] = useState(false);
    const [letter, setLetter] = useState("-");
    var position = [];
    const context = {state, setState, special, setSpecial, letter, setLetter, position};

    return context;
}



export function RenderCell(context) {
    let classes =   (context.state ? 'Cell' : 'Space') + 
                    (context.special ? ' Cell-Glow' : ' Cell-Input');

    let content = context.letter;

    function handleToggle(context) {
        context.setSpecial(!context.special);
    }

    return (
        <td className={classes} onClick={() => {handleToggle(context);}}>
            <p className="CellContent">
                {content}
            </p>
        </td>
    )
    // return (
